Module.Content:
		
		#requireResource("${atlassian.jira.plugin.key}:charts-gadget")
        #includeResources()
		<script type="text/javascript">
		gadgets.window.setTitle("__MSG_hudson.gadget.build.trend.title__");
		(function () {
			AJS.Gadget({
				baseUrl: "__ATLASSIAN_BASE_URL__",
				useOauth: "/rest/gadget/1.0/currentUser",
				config: {
					descriptor: function (args) {
						var gadget = this;
						gadgets.window.setTitle("__MSG_hudson.gadget.build.trend.title__");
						return  {
							action: "/rest/hudson/1.0/hudsonCharts/validate",
                            theme : function() {
                                if (gadgets.window.getViewportDimensions().width < 450) {
                                    return "gdt top-label";
                                } else {
                                    return "gdt";
                                }
                            }(),
							fields: [
								AJS.gadget.fields.projectPicker(gadget, "projectId", args.options),
								AJS.gadget.fields.nowConfigured()
							]
						};
					},
					args: function() {
						return [ {
								key: "options",
								ajaxOptions: "/rest/gadget/1.0/filtersAndProjects?showFilters=false"
							}
						];
					}()
				},
				view: {
					enableReload: true,
					onResizeReload: true,
					template: function (args) {
						var gadget = this;
						gadget.getView().empty();
                        gadget.getView().attr("id", "hudson-build-trend-content");
						var baseUrl = AJS.$.ajaxSettings.baseUrl;
						if (args.data.hasErrors) {
							gadgets.window.setTitle("__MSG_hudson.gadget.build.trend.title__");
							gadget.getView().append(AJS.gadget.hudson.common.generateErrorMessageBox(gadget, args.data.errors));
						} else {
							var server = args.data.server;
							var project = args.data.project;
							var chart = args.data.chart;
							gadgets.window.setTitle(AJS.format("__MSG_hudson.gadget.build.trend.project.title__", project.name));
							if (chart.generated) {
								AJS.gadget.hudson.charts.displayChartImage(gadget, baseUrl, server, project, chart);
							} else {
								gadget.getView().append(
									AJS.gadget.hudson.common.generateErrorMessageBox(["__MSG_hudson.gadget.error.chart.not.generated__"])
								);
							}
						}
						gadget.resize();
					},
					args: [{
						key: "data",
						ajaxOptions: function () {
							var gadget = this;
							return {
								url: "/rest/hudson/1.0/hudsonCharts/generateBuildTrend",
								data: {
									projectId: gadget.getPref("projectId")
								}
							};
						}
					}]
				}
			});
		})();
		</script>
