Module.Content:
#requireResource("${atlassian.plugin.key}:hudson-status-gadget")
#includeResources()
<script type="text/javascript">
gadgets.window.setTitle("__MSG_hudson.gadget.status.title__");
(function () {
	AJS.Gadget({
		baseUrl: "__ATLASSIAN_BASE_URL__",
		useOauth: "/rest/gadget/1.0/currentUser",
		config: {
			descriptor: function (args) {
				var gadget = this;
				gadgets.window.setTitle("__MSG_hudson.gadget.status.title__");
				return  {
					action: "",
					theme : AJS.hudson.gadget.config.getThemeConfiguration(gadgets),
					fields: AJS.hudson.gadget.config.getServerOnlyFields(gadget)
				};
			},
			args: []
		},
		view: {
			enableReload: true,
			onResizeReload: true,
			template: function (args) {
				var gadget = this;
				gadget.getView().empty();
				for (var job in args.jobs) {
					var jobDiv = AJS.hudson.utils.createDiv().addClass('job');
					var result = AJS.hudson.utils.createDiv().addClass('result');
					if (job.lastBuild.result !== "undefined") {
						result.addClass(job.lastBuild.result.toLowerCase());
					} else {
						result.addClass('notbuild');
					}
					var name = AJS.hudson.utils.createDiv().addClass('name');
					// TODO add name
					result.append(name);
					var details = AJS.hudson.utils.createDiv().addClass('details');
					// TODO add details
					results.append(details);
					jobDiv.append(result);
					gadget.getView().append(jobDiv);
				}
				gadget.resize();
			},
			args: [{
				key: "jobs",
				ajaxOptions: function () {
					var gadget = this;
					var hudsonServer = AJS.hudson.gadget.utils.parseServer(gadget.getPref("server"));
					return AJS.hudson.gadget.utils.getAjaxOptions(hudsonServer, "/apiv2/jobs/all");
				}
			}]
		}
	});
})();
</script>
